let currentLang = 'ar';
let activeAudio = null;
let currentCategory = '';
let sessionCounts = {};

/**
 * Handle Light/Dark Mode Toggle
 */
function toggleTheme() {
    const body = document.body;
    const themeIcon = document.getElementById('theme-icon');
    
    body.classList.toggle('dark-mode');
    
    if (body.classList.contains('dark-mode')) {
        themeIcon.innerText = 'â˜€ï¸'; 
        localStorage.setItem('theme', 'dark');
    } else {
        themeIcon.innerText = 'ðŸŒ™';
        localStorage.setItem('theme', 'light');
    }
}

/**
 * Play audio preview on home cards
 */
function playHomePreview(event, category, btn) {
    event.stopPropagation();
    const audioPath = translations[currentLang][`${category}_intro`];
    handleAudio(audioPath, btn);
}

/**
 * Handle Language Switching and UI updates
 */
function changeLanguage(lang, btnElement) {
    currentLang = lang;
    const html = document.documentElement;
    const indicator = document.getElementById('lang-indicator');
    
    html.dir = (lang === 'ar') ? 'rtl' : 'ltr';
    html.lang = lang;

    document.querySelectorAll('[data-i18n]').forEach(el => {
        const key = el.getAttribute('data-i18n');
        if (translations[lang][key]) {
            el.innerText = translations[lang][key];
        }
    });

    if (btnElement && indicator) {
        document.querySelectorAll('.lang-btn').forEach(btn => btn.classList.remove('active'));
        btnElement.classList.add('active');
        
        const offset = btnElement.offsetLeft;
        const width = btnElement.offsetWidth;
        indicator.style.width = `${width}px`;
        indicator.style.transform = `translateX(${offset}px)`;
    }

    if (currentCategory) {
        const pageTitle = document.getElementById('page-title');
        if (pageTitle && translations[lang][currentCategory]) {
            pageTitle.innerText = translations[lang][currentCategory];
        }
        renderCards(currentCategory);
    }
}

/**
 * Render Athkar cards
 */
function renderCards(category) {
    const list = document.getElementById('athkar-list');
    if (!database[category]) return;

    // Hide only the secondary (ðŸŽ§) audio button for morning/evening/sleep
    const hideSecondaryAudio = ['morning', 'evening', 'sleep'].includes(category);

    list.innerHTML = database[category].map(item => `
        <div class="card athkar-card" id="card-${item.id}">
            ${item.intro ? `<p class="intro-text text-rtl">${item.intro}</p>` : ''}
            <p class="arabic-text text-rtl">${item.text}</p>
            <div class="actions">
                <button class="audio-btn secondary" data-type="primary" onclick="handleAudio('${item.audio}', this)">
                    ðŸ”Š ${translations[currentLang].listen}
                </button>
                
                ${hideSecondaryAudio ? '' : `
                <button class="audio-btn secondary" data-type="secondary" onclick="handleAudio('${item.audio2 || item.audio}', this)">
                    ðŸŽ§ ${translations[currentLang].listen}
                </button>
                `}

                <button class="count-btn" id="btn-${item.id}" onclick="handleCount(${item.id})">
                    ${item.target}
                </button>
            </div>
        </div>
    `).join('');
}

// --- CORE APP LOGIC ---

function updateTheme(category) {
    const body = document.body;
    // Remove existing theme classes but preserve 'dark-mode'
    body.classList.remove('theme-morning', 'theme-evening', 'theme-sleep', 'theme-prayer');
    if (category) body.classList.add(`theme-${category}`);
}

function loadAthkar(category) {
    currentCategory = category;
    sessionCounts[category] = 0;
    
    document.getElementById('main-menu').classList.add('hidden');
    document.getElementById('page-title').classList.add('hidden');
    document.getElementById('athkar-list').classList.remove('hidden');
    document.getElementById('progress-container').classList.remove('hidden');

    updateTheme(category);
    renderCards(category);
    updateProgress();
    window.scrollTo({ top: 0, behavior: 'smooth' });
}

function handleCount(id) {
    const btn = document.getElementById(`btn-${id}`);
    const card = document.getElementById(`card-${id}`);
    if (!btn || btn.innerText === "âœ“") return;

    let val = parseInt(btn.innerText);
    if (val > 0) {
        val--;
        btn.innerText = val === 0 ? "âœ“" : val;
        
        if (navigator.vibrate) navigator.vibrate(50);
        
        if (val === 0) {
            btn.classList.add('done');
            card.classList.add('completed');
            sessionCounts[currentCategory]++;
            updateProgress();
        }
    }
}

function updateProgress() {
    if (!currentCategory || !database[currentCategory]) return;
    const total = database[currentCategory].length;
    const progress = (sessionCounts[currentCategory] / total) * 100;
    const bar = document.getElementById('progress-bar');
    if (bar) bar.style.width = `${progress}%`;
}

function handleAudio(src, btn) {
    const isSecondary = btn.getAttribute('data-type') === 'secondary' || btn.innerText.includes('ðŸŽ§');
    const playIcon = isSecondary ? "ðŸŽ§" : "ðŸ”Š";
    const cleanListenText = `${playIcon} ${translations[currentLang].listen}`;
    
    if (activeAudio) {
        activeAudio.pause();
        
        document.querySelectorAll('.audio-btn, .home-audio-btn').forEach(b => {
            if (b.innerText.includes("â¸") || b.innerText.includes("â³")) {
                const bIsSecondary = b.getAttribute('data-type') === 'secondary' || b.classList.contains('home-audio-btn');
                const bIcon = bIsSecondary ? "ðŸŽ§" : "ðŸ”Š";
                b.innerText = `${bIcon} ${translations[currentLang].listen}`;
            }
        });

        if (activeAudio.src.includes(src)) {
            activeAudio = null;
            btn.innerText = cleanListenText;
            return;
        }
    }
    
    activeAudio = new Audio(src);
    btn.innerText = "â³ " + translations[currentLang].loading;
    cacheAudioAsset(src);
    
    activeAudio.play()
        .then(() => { 
            btn.innerText = "â¸ " + translations[currentLang].stop; 
        })
        .catch(() => { 
            btn.innerText = "âŒ Error"; 
            setTimeout(() => { btn.innerText = cleanListenText; }, 2000);
        });

    activeAudio.onended = () => {
        btn.innerText = cleanListenText;
        activeAudio = null;
    };
}

function cacheAudioAsset(src) {
    if (!('serviceWorker' in navigator)) return;
    const controller = navigator.serviceWorker.controller;
    if (!controller) return;
    controller.postMessage({ type: 'CACHE_AUDIO', url: src });
}

function cacheAllAudioAssets() {
    if (!('serviceWorker' in navigator)) return;

    const urls = new Set();
    Object.values(translations).forEach(lang => {
        Object.keys(lang).forEach(key => {
            if (key.endsWith('_intro')) {
                urls.add(lang[key]);
            }
        });
    });

    Object.values(database).forEach(list => {
        list.forEach(item => {
            if (item.audio) urls.add(item.audio);
            if (item.audio2) urls.add(item.audio2);
        });
    });

    navigator.serviceWorker.ready.then(reg => {
        if (!reg.active) return;
        reg.active.postMessage({ type: 'CACHE_AUDIO_LIST', urls: Array.from(urls) });
        localStorage.setItem('offline_audio_cached', 'true');
    });
}

function showMainMenu() {
    document.getElementById('main-menu').classList.remove('hidden');
    document.getElementById('page-title').classList.remove('hidden');
    document.getElementById('athkar-list').classList.add('hidden');
    document.getElementById('progress-container').classList.add('hidden');
    
    const bar = document.getElementById('progress-bar');
    if (bar) bar.style.width = "0%";
    
    updateTheme(null); // Clear category theme
    window.scrollTo({ top: 0, behavior: 'smooth' });
    
    if(activeAudio) { 
        activeAudio.pause(); 
        activeAudio = null; 
    }
}

function resetAll() { 
    if(currentCategory) loadAthkar(currentCategory); 
}

/**
 * Initialization on Load
 */
document.addEventListener('DOMContentLoaded', () => {
    // 1. Restore Theme Preference
    const savedTheme = localStorage.getItem('theme');
    if (savedTheme === 'dark') {
        document.body.classList.add('dark-mode');
        document.getElementById('theme-icon').innerText = 'â˜€ï¸';
    }

    // 2. Initial Language Setup
    const activeBtn = document.querySelector('.lang-btn.active');
    if (activeBtn) {
        changeLanguage(currentLang, activeBtn);
    }

    // 3. Register Service Worker for offline support
    if ('serviceWorker' in navigator) {
        navigator.serviceWorker.register('./sw.js').catch(() => {
            // Silent fail; app still works without SW in native shell.
        });
    }

    // 4. Cache all audio for full offline use (first run only)
    if (!localStorage.getItem('offline_audio_cached')) {
        cacheAllAudioAssets();
    }
});

